mapper.select:
select max(re_step)
from board_reply
where rep = #{rep};

select max(re_step)
from board_reply
where rep = #{rep} and re_level = #{re_level};

mapper.update:
update board
set readcount = readcount + 1
where seq = #{seq};

update board_reply
set re_step = re_step + 1
where rep = #{rep} and re_step >= #{re_step};

mapper.select:
select seq, subject, content, name, reg_date, readCount
from board
where seq = #{seq};

mapper.update:
update board
set subject = #{subject}, content = #{content}
where seq = #{seq};

mapper.select:
select rseq, bseq, name, content, rep, reg_date, re_step, re_level
from board_reply
where bseq = #{bseq}
order by rep, re_step;
